import{_ as a,o as e,c as t,Q as r}from"./chunks/framework.0bd140f3.js";const u=JSON.parse('{"title":"团建","description":"","frontmatter":{},"headers":[],"relativePath":"41 关于组织团建/README.md","filePath":"41 关于组织团建/README.md"}'),o={name:"41 关于组织团建/README.md"},n=r('<h1 id="团建" tabindex="-1">团建 <a class="header-anchor" href="#团建" aria-label="Permalink to &quot;团建&quot;">​</a></h1><h2 id="群体与团队" tabindex="-1">群体与团队 <a class="header-anchor" href="#群体与团队" aria-label="Permalink to &quot;群体与团队&quot;">​</a></h2><p>我们在说团建之前，先明确两个概念：群体与团队。在管理学上，群体（Group）和团队（Team）是两个不同的概念。群体，也可以被称为“团伙”，就是一群人，成员之间可以有共同的目标，但彼此之间没有什么关联。团队，是一群有共同目标、成员之间有协同配合关系的组织。成员之间有配合、有默契，是团队和群体最大的分别。</p><h2 id="相识" tabindex="-1">相识 <a class="header-anchor" href="#相识" aria-label="Permalink to &quot;相识&quot;">​</a></h2><h2 id="相知" tabindex="-1">相知 <a class="header-anchor" href="#相知" aria-label="Permalink to &quot;相知&quot;">​</a></h2><h2 id="相依" tabindex="-1">相依 <a class="header-anchor" href="#相依" aria-label="Permalink to &quot;相依&quot;">​</a></h2><h3 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-label="Permalink to &quot;参考&quot;">​</a></h3><p><a href="https://mp.weixin.qq.com/s/tQHjbpsdonnJmY1cMdcxmg" target="_blank" rel="noreferrer">https://mp.weixin.qq.com/s/tQHjbpsdonnJmY1cMdcxmg</a></p>',8),i=[n];function h(s,c,d,l,_,m){return e(),t("div",null,i)}const f=a(o,[["render",h]]);export{u as __pageData,f as default};
