import{_ as e,o as a,c as t,Q as o}from"./chunks/framework.0f4e1e9e.js";const s="/static/目录结构规范.278d23e6.jpg",b=JSON.parse('{"title":"项目目录与文件结构规范","description":"","frontmatter":{},"headers":[],"relativePath":"frontend/1 前端工程化/3 规范化/开发规范化-项目目录与文件结构规范.md","filePath":"frontend/1 前端工程化/3 规范化/开发规范化-项目目录与文件结构规范.md"}'),r={name:"frontend/1 前端工程化/3 规范化/开发规范化-项目目录与文件结构规范.md"},i=o('<h1 id="项目目录与文件结构规范" tabindex="-1">项目目录与文件结构规范 <a class="header-anchor" href="#项目目录与文件结构规范" aria-label="Permalink to &quot;项目目录与文件结构规范&quot;">​</a></h1><h2 id="_1、目录结构规范" tabindex="-1">1、目录结构规范 <a class="header-anchor" href="#_1、目录结构规范" aria-label="Permalink to &quot;1、目录结构规范&quot;">​</a></h2><p><img src="'+s+'" alt=""></p><h2 id="_2、文件-文件夹命名规范" tabindex="-1">2、文件/文件夹命名规范 <a class="header-anchor" href="#_2、文件-文件夹命名规范" aria-label="Permalink to &quot;2、文件/文件夹命名规范&quot;">​</a></h2><ol><li>文件/文件夹命名</li></ol><p>1)最好用一个单词描述</p><p>常用项目命名omi、element、master、project、test、vue、iview</p><p>二级目录build、static、config、src、examples、base、common、issues、assert</p><p>三级目录libs、models、plugins、skins、images、css、js</p><p>常用文件命名 index、shopping（购物）、 share（分享）、integral（积分）、advertisement（广告）、pay（支付）、community（社区）、game、docs、bussiness</p><p>2)如果一个单词描述不了，用2个词（名词加动词）</p><p>color-pick、button-groups、date-picker、option-grounp、jquery-select、jquery-swiper等等反正就是自我想象</p><p>3)中间用-或者_连接为了方便归类、一目了然</p><p>node_models、async-demo、array-union、array-differ、babel-each</p><p>例如：</p><p>移动广告(mobile-advertisement) 移动社交(mobile-social) 移动电子商务(mobile-bussiness) 手机游戏(mobile-game) 手机电视(mobile-tv) 移动电子阅读(mobile-reading) 手机搜索(mobile-search) 移动支付(mobile-pay) 手机内容共享(mobile-share)</p><p>文件/文件夹都可以用名词+需要的动词命名，达到见词知意</p><p><a href="https://zhuanlan.zhihu.com/p/27135630" target="_blank" rel="noreferrer">https://zhuanlan.zhihu.com/p/27135630</a></p>',18),n=[i];function p(_,l,c,m,d,h){return a(),t("div",null,n)}const f=e(r,[["render",p]]);export{b as __pageData,f as default};
