import{_ as e,o as t,c as l,O as i}from"./chunks/framework.d9e2f5d0.js";const r="/static/8ed437ee-0b1b-493a-a918-94fbfa4adf28.93dbafca.jpg",m=JSON.parse('{"title":"进阶篇","description":"","frontmatter":{},"headers":[],"relativePath":"frontend/2 前端优化/2 进阶篇/README.md","filePath":"frontend/2 前端优化/2 进阶篇/README.md"}'),o={name:"frontend/2 前端优化/2 进阶篇/README.md"};function n(h,a,s,d,c,u){return t(),l("div",null,a[0]||(a[0]=[i('<h1 id="进阶篇" tabindex="-1">进阶篇 <a class="header-anchor" href="#进阶篇" aria-label="Permalink to &quot;进阶篇&quot;">​</a></h1><h2 id="网络优化" tabindex="-1">网络优化 <a class="header-anchor" href="#网络优化" aria-label="Permalink to &quot;网络优化&quot;">​</a></h2><ul><li>利用combo技术，实现服务端打包代码资源。</li><li>APP上通过网络推包，html、js、css资源加载本地化。</li></ul><h2 id="页面渲染优化" tabindex="-1">页面渲染优化 <a class="header-anchor" href="#页面渲染优化" aria-label="Permalink to &quot;页面渲染优化&quot;">​</a></h2><ul><li>flutter渲染</li><li>北海渲染引擎</li></ul><h2 id="用户体验优化-研发视角" tabindex="-1">用户体验优化（研发视角） <a class="header-anchor" href="#用户体验优化-研发视角" aria-label="Permalink to &quot;用户体验优化（研发视角）&quot;">​</a></h2><h3 id="ui-交互统一" tabindex="-1">UI/交互统一 <a class="header-anchor" href="#ui-交互统一" aria-label="Permalink to &quot;UI/交互统一&quot;">​</a></h3><h3 id="制定一套适合自己公司业务发展的开发规范" tabindex="-1">制定一套适合自己公司业务发展的开发规范 <a class="header-anchor" href="#制定一套适合自己公司业务发展的开发规范" aria-label="Permalink to &quot;制定一套适合自己公司业务发展的开发规范&quot;">​</a></h3><h4 id="背景和理念" tabindex="-1">背景和理念 <a class="header-anchor" href="#背景和理念" aria-label="Permalink to &quot;背景和理念&quot;">​</a></h4><p>图形用户界面（Graphics User Interfae）简称GUI。使用图形方式，借助菜单、按钮等标准界面元素和鼠标操作，帮助用户方便地向计算机系统发出指令，启动操作，并将系统运行的结果同样以图形方式显示给用户。GUI中的三个基本元素：容器、组件和布局管理器。开发者通过这些基本元素和布局可开发出各种花里胡哨的界面和交互效果。</p><p>如下图所示都是搜索功能模块不同的团队开发有不同的展现形式和交互。</p><p><img src="'+r+'" alt=""></p><p>对于面向商家、企业级、业务部门提供的服务产品来说带来不少麻烦，不同的团队开发出来产品视觉交互体验不一致，缺少框架规范，产品、UI、研发没有标准，重复造轮子。</p><p>为了解决上述的痛点，团队应该结合自身的业务制定Web GUI编程规范和标准，从而达到视觉交互一致性。</p><h4 id="ui规范" tabindex="-1">UI规范 <a class="header-anchor" href="#ui规范" aria-label="Permalink to &quot;UI规范&quot;">​</a></h4><ul><li>Page</li><li>Form</li><li>FormItem</li><li>Button</li></ul><h4 id="交互规范" tabindex="-1">交互规范 <a class="header-anchor" href="#交互规范" aria-label="Permalink to &quot;交互规范&quot;">​</a></h4><ul><li>封闭交互（一个体系有且仅有一种交互）</li><li>编码封闭（DML,描述“本质”,代码和页面一一映射,没有第二种写法）</li></ul><p>保持开放原则80%系统组件（XComponent），20%业务中复杂组件单独实现（SysComponent）</p>',19)]))}const f=e(o,[["render",n]]);export{m as __pageData,f as default};
