import{_ as i,o as e,c as o,O as a}from"./chunks/framework.d9e2f5d0.js";const p=JSON.parse('{"title":"前端管理","description":"","frontmatter":{},"headers":[],"relativePath":"frontend/40 关于前端管理.md","filePath":"frontend/40 关于前端管理.md"}'),t={name:"frontend/40 关于前端管理.md"};function r(n,l,s,d,c,_){return e(),o("div",null,l[0]||(l[0]=[a('<h1 id="前端管理" tabindex="-1">前端管理 <a class="header-anchor" href="#前端管理" aria-label="Permalink to &quot;前端管理&quot;">​</a></h1><ul><li>任务管理：做事 <ol><li>团队内部根据业务线分配，每个同学有主要负责的业务线，业务线开发主要是实现产品需求，要对业务有深入了解每次有新的任务首先分配给主要负责的业务线同学，主要负责的同学为业务线结果负责。</li><li>也有另外一个同学次要了解此业务线，当有紧急情况或业务比较忙的时候可以顶替，也可以促进组内各同学之间的交流学习。</li><li>每个人负责的业务线也不是一层不变的，我们会定期的召开会议，比如半年、一年与组员沟通，了解组员的真实需求，根据组员的需求想要要做什么进行调配。</li></ol></li><li>规划管理：规划目标 <ol><li>目标： <ol><li>拉通前端整体的技术框架和技术。</li><li>负责前端的整体的技术能力提升和学习交流。</li></ol></li><li>围绕着技术中心建设目标和业务发展需求，结合前端现有的问题和未来的发展，前端技术组制定以下规划。规划从架构方面和开发方面去做一些事情。 <ol><li>架构主要是为了能够支持支撑业务/架构业务。 <ol><li>微前端架构设计</li></ol></li><li>开发主要是为研发过程中提高效率。 <ol><li>抽象SDK工具包，如网络层，存储层等</li><li>自动化测试工具等</li><li>持续交付工具等</li></ol></li></ol></li></ol></li><li>团队建设：带人 <ol><li>团队文化（文化宣导：打造简单、坦诚的文化氛围） <ul><li>所谓坦诚：就是把问题摆到桌面上，而不是互相猜忌，弄得组织内部自成壁垒。</li><li>所谓简单：就是要对事不对人，看见别人做得不对的地方，为了拿到工作的结果，能做到直言不讳，指出问题原因(切忌不要在言语上涉及人身攻击)。就事论事，想着怎么推进进展和解决问题，沟通变得简单了，组织的效率也就大幅的得到提升了。</li></ul></li><li>团队成长-学习提升 <ol><li>指定学习提升计划</li></ol></li><li>激励 - 成就可视化</li><li>团建活动策划</li><li>团队问题处理</li></ol></li></ul><h3 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-label="Permalink to &quot;参考&quot;">​</a></h3><p><a href="https://juejin.cn/post/7024901851078197255?searchId=20240523092602C9263E8271EE4846B781" target="_blank" rel="noreferrer">https://juejin.cn/post/7024901851078197255?searchId=20240523092602C9263E8271EE4846B781</a></p>',4)]))}const f=i(t,[["render",r]]);export{p as __pageData,f as default};
