import{_ as a,o as t,c as o,Q as e}from"./chunks/framework.0f4e1e9e.js";const c="/static/ApacheTomcatB.5.63.78fbfe11.png",r="/static/Toncat8.5.65.11d78d5b.jpg",u=JSON.parse('{"title":"Tomcat 基础","description":"","frontmatter":{},"headers":[],"relativePath":"server/Tomcat/README.md","filePath":"server/Tomcat/README.md"}'),s={name:"server/Tomcat/README.md"},h=e('<h1 id="tomcat-基础" tabindex="-1">Tomcat 基础 <a class="header-anchor" href="#tomcat-基础" aria-label="Permalink to &quot;Tomcat 基础&quot;">​</a></h1><h2 id="一、安装tomcat" tabindex="-1">一、安装Tomcat <a class="header-anchor" href="#一、安装tomcat" aria-label="Permalink to &quot;一、安装Tomcat&quot;">​</a></h2><p><a href="https://tomcat.apache.org/" target="_blank" rel="noreferrer">Tomcat官方下载地址</a></p><h2 id="二、用终端-terminal-直接打开tomcat了" tabindex="-1">二、用终端（Terminal）直接打开Tomcat了 <a class="header-anchor" href="#二、用终端-terminal-直接打开tomcat了" aria-label="Permalink to &quot;二、用终端（Terminal）直接打开Tomcat了&quot;">​</a></h2><p>1、进入Tomcat的bin目录下：终端输入<code>cd /Library/Tomcat/bin</code> ，输完回车<br> 2、授权bin目录下的所有操作：终端输入<code>sudo chmod 755 *.sh</code>，输完回车<br> 3、这时要输入密码，输完回车<br> 4、这时候就可以开启Tomcat了，终端输入<code>sudo sh ./startup.sh</code>，输完回车</p><h2 id="三、到浏览器输入网址-localhost-8080-若出现了下面的画面就证明成功了" tabindex="-1">三、到浏览器输入网址 localhost:8080 ，若出现了下面的画面就证明成功了 <a class="header-anchor" href="#三、到浏览器输入网址-localhost-8080-若出现了下面的画面就证明成功了" aria-label="Permalink to &quot;三、到浏览器输入网址 localhost:8080  ，若出现了下面的画面就证明成功了&quot;">​</a></h2><p>￼<img src="'+c+'" alt=""></p><h2 id="四、关闭tomcat-用终端输入sh-shutdown-sh-回车即可关闭" tabindex="-1">四、关闭Tomcat，用终端输入sh ./shutdown.sh，回车即可关闭 <a class="header-anchor" href="#四、关闭tomcat-用终端输入sh-shutdown-sh-回车即可关闭" aria-label="Permalink to &quot;四、关闭Tomcat，用终端输入sh ./shutdown.sh，回车即可关闭&quot;">​</a></h2><p><a href="https://www.jianshu.com/p/69496fb3495e" target="_blank" rel="noreferrer">MAC下安装配置Tomcat（小白向）</a></p><p>MAC安装Tomcat如何配置账号密码？？？</p><p>找到tomcat主目录下的子目录conf里面的tomcat-users.xml文件，在此文件里修改。</p><h2 id="五、查看tomcat版本号" tabindex="-1">五、查看Tomcat版本号 <a class="header-anchor" href="#五、查看tomcat版本号" aria-label="Permalink to &quot;五、查看Tomcat版本号&quot;">​</a></h2><p>输入命令：<code>sh /Library/Tomcat/bin/catalina.sh version</code></p><p><img src="'+r+'" alt=""></p>',14),m=[h];function n(i,l,d,p,_,T){return t(),o("div",null,m)}const f=a(s,[["render",n]]);export{u as __pageData,f as default};
